import Managerdropdown from './Managerdropdown';
import axios from "axios";
import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import { isExpired } from 'react-jwt';
function Editmanager(props) {
    const navigate = useNavigate();
    // const [employee, setEmployee] = useState(
    //     {
    //         firstName: "",
    //         lastName: "",
    //         password: "",
    //         mobileNumber: "",
    //         // gender: "",
    //         dateOfBirth: "",
    //         address: "",
    //         city: "",
    //         state: "",
    //         pinCode: "",
    //         country: "",
    //         role: "",

    //     }
    // );
    const[firstName,setfirstName]=useState('');
    const[lastName,setlastName]=useState('');
    // const[emailId,setemailId]=useState('');
    const[employeeId,setemployeeId]=useState('');
    const[password,setpassword]=useState('');
    const[role,setrole]=useState('');
    const[dateOfBirth,setdateOfBirth]=useState('');
    const[address,setaddress]=useState('');
    const[city,setcity]=useState('');
    const[state,setstate]=useState('');
    const[pinCode,setpinCode]=useState('');
    const[country,setcountry]=useState('');
    if(isExpired(localStorage.getItem('jwtToken'))){
        alert("Session Timeout Please Login again");
        window.location.href = "/";
      }
      const firstNamehandler=(event)=>{
        setfirstName(event.target.value);
       }
       const lastNamehandler=(event)=>{
        setlastName(event.target.value);
       }
       const emailIdhandler=(event)=>{
        setemailId(event.target.value);
       }
       const employeeIdhandler=(event)=>{
        setemployeeId(event.target.value);
       }
       const passwordhandler=(event)=>{
        setpassword(event.target.value);
       }
       const rolehandler=(event)=>{
        setrole(event.target.value);
       }
       const dateOfBirthhandler=(event)=>{
        setdateOfBirth(event.target.value);
       }
       const addresshandler=(event)=>{
        setaddress(event.target.value);
       }
       const cityhandler=(event)=>{
        setcity(event.target.value);
       }
       const statehandler=(event)=>{
        setstate(event.target.value);
       }
       const pinCodehandler=(event)=>{
        setpinCode(event.target.value);
       }
       const countryhandler=(event)=>{
        setcountry(event.target.value);
       }

  const [gender,setGender]=useState('');
    const { emailId } = useParams();
    useEffect(() => {
        const loadEmployee = async () => {
            const reqdata = await fetch(`http://localhost:8080/admin/manager/${emailId}`);

            const res = reqdata.json();
            setEmployee(await res);
        }
        loadEmployee();
    },
        []);




    //const { firstName, lastName, password, mobileNumber,  dateOfBirth, address, city, state, pinCode, country, role } = employee;

    const onInputChange = (e) => {
        setEmployee({
            ...employee,
            [e.target.name]: e.target.value
        });
    }
     const onGender=(e)=>{
        setGender(e.target.value);
     }
    const submithandler = async (e) => {
        e.preventDefault();
        console.log("hello hi ");
        console.log(employee);
        const response = await axios.put(`http://localhost:8080/admin/manager/put/${emailId}`, {
            "firstName":firstName,
            "lastName":lastName,
            "password":password,
            "mobileNumber":mobileNumber,
            "gender":gender,
            "dateOfBirth":dateOfBirth,
            "address":address,
            "city":city,
            "state":state,
            "contry":country,
            "pinCode":pinCode,



        });
        navigate("/mngprofile");
    };

    return (
        <div>
            <Managerdropdown></Managerdropdown>
            <div style={{ marginTop: '4rem' }}>
                <div className="container">
                    <div className="row">
                        <div className="col-md-6 offset-md-3 border rounded p-4 mt-2 shadow">
                            <h2 className="text-center m-4"> Edit Profile  </h2>
                            <form className="bg-transparent" onSubmit={submithandler}>
                                <div className='row'>
                                    <div className='col-md-5'>
                                        <div className=" mb-1" style={{ display: 'inline-block', width: '100%' }}>
                                            <b><label htmlFor="firstName" className="form-label" style={{ display: 'inline-block', width: '30%', marginLeft: '10px', textalign: 'left' }}>FirstName</label></b>
                                            <input
                                                type={"text"}
                                                className="form-control"
                                                placeholder="Enter Firstname"
                                                name="firstName"
                                                value={firstName}
                                                autoComplete="off"
                                                onChange={onInputChange}
                                                style={{ display: 'inline-block', width: '230px', height: '2px', marginLeft: '5px', textalign: 'center', padding: '18px', fontSize: '14px' }}
                                            ></input>

                                        </div>
                                    </div>
                                    <div className='col-md-5'>
                                        <div className="mb-1">
                                            <b><label htmlFor="lastName" className="form-label" style={{ textalign: 'center' }}>LastName</label></b>
                                            <input
                                                type={"text"}
                                                className="form-control"
                                                placeholder="Enter Lastname"
                                                name="lastName"
                                                value={lastName}
                                                onChange={onInputChange}
                                                autoComplete="off"
                                                style={{ width: '230px', height: '2px', marginLeft: '50px', padding: '18px', fontSize: '14px' }}
                                            ></input>

                                        </div>
                                    </div>
                                </div>


                                <div className='row'>
                                    <div className='col-md-5'>
                                        <div className="mb-1">
                                            <b><label htmlFor="role" className="form-label form-floating">Department</label></b>
                                            <input
                                                type={"text"}
                                                className="form-control"
                                                placeholder="Enter your lastname"
                                                name="role"
                                                value={role} disabled
                                                onChange={(e) => onInputChange(e)}
                                                style={{ display: 'inline-block', width: '230px', height: '2px', marginLeft: '5px', textalign: 'center', padding: '18px', fontSize: '14px' }}
                                            ></input>

                                        </div>
                                    </div>
                                    <div className='col-md-5'>
                                        <div className="mb-1">
                                            <b><label htmlFor="dateOfBirth" className="form-label">DOB</label></b>

                                            <input
                                                type={"Date"}
                                                className="form-control"
                                                placeholder="Enter your dateOfBirth"
                                                name="dateOfBirth"
                                                value={dateOfBirth}
                                                onChange={(e) => onInputChange(e)}
                                                autoComplete="off"
                                                style={{ width: '230px', height: '2px', marginLeft: '50px', padding: '18px', fontSize: '14px' }}
                                            ></input>
                                            {/* {errors.password && <div style={{color:'red'}}>{errors.password}
                                            </div>} */}

                                        </div>

                                    </div>
                                </div>
                                <div className='row'>
                                    <div className='col-md-5'>
                                        <div className="mb-1">
                                            <b><label htmlFor="mobileNumber" className="form-label">Mobile Number</label></b>
                                            <input
                                                type={"text"}
                                                className="form-control"
                                                placeholder="Enter your mobileNumber"
                                                name="mobileNumber"
                                                value={mobileNumber}
                                                onChange={(e) => onInputChange(e)}
                                                autoComplete="off"
                                                style={{ width: '230px', height: '2px', marginLeft: '5px', textalign: 'center', padding: '18px', fontSize: '14px' }}
                                            ></input>
                                            {/* {errors.password && <div style={{ color: 'red' }}>{errors.password}
                                        </div>} */}

                                        </div>
                                    </div>
                                    <div className='col-md-5'>
                                        <div className="mb-1">
                                            <b>choose your gender:</b>
                                            <select value={gender}  name="gender" onChange={(e) => onGender(e)}>
                                            <option value="">select  an option </option>
                                            <option value="male" >Male</option>
                                            <option value="female">Female</option>
                                            </select>
                                            {/* <b> <label htmlFor="gender" className="form-label">Gender</label></b>
                                            <input
                                                type={"text"}
                                                className="form-control"
                                                placeholder="Enter your gender"
                                                name="gender"
                                                value={gender}
                                                onChange={(e) => onInputChange(e)}
                                                autoComplete="off"
                                                style={{ width: '230px', height: '2px', marginLeft: '50px', padding: '18px', fontSize: '14px' }}
                                            ></input> */}
                                            {/* {errors.password && <div style={{color:'red'}}>{errors.password}
                                            </div>} */}

                                        </div>

                                    </div>
                                </div>





                                <div className='row'>

                                    <div className='col-md-5'>
                                        <div className="mb-1">
                                            <b>  <label htmlFor="address" className="form-label" style={{ textalign: 'center' }}>Address</label></b>
                                            <input
                                                type={"text"}
                                                className="form-control"
                                                placeholder="Enter your address"
                                                name="address"
                                                value={address}
                                                onChange={(e) => onInputChange(e)}
                                                autoComplete="off"
                                                style={{ width: '230px', height: '2px', marginLeft: '5px', textalign: 'center', padding: '18px', fontSize: '14px' }}
                                            ></input>

                                        </div>

                                    </div>
                                    <div className='col-md-5'>
                                        <div className="mb-1">
                                            <b> <label htmlFor="city" className="form-label">City</label></b>
                                            <input
                                                type={"text"}
                                                className="form-control"
                                                placeholder="Enter your city"
                                                name="city"
                                                value={city}
                                                onChange={(e) => onInputChange(e)}
                                                autoComplete="off"
                                                style={{ width: '230px', height: '2px', marginLeft: '50px', padding: '18px', fontSize: '14px' }}
                                            ></input>


                                        </div>

                                    </div>
                                </div>



                                <div className='row'>


                                    <div className='col-md-5'>
                                        <div className="mb-1">
                                            <b> <label htmlFor="pinCode" className="form-label">Pincode</label></b>
                                            <input
                                                type={"text"}
                                                className="form-control"
                                                placeholder="Enter your pincode"
                                                name="pinCode"
                                                value={pinCode}
                                                onChange={(e) => onInputChange(e)}
                                                autoComplete="off"
                                                style={{ width: '230px', height: '2px', marginLeft: '5px', textalign: 'center', padding: '18px', fontSize: '14px' }}
                                            ></input>

                                        </div>
                                    </div>

                                    <div className='col-md-5'>
                                        <div className="mb-1">
                                            <b>  <label htmlFor="country" className="form-label">Country</label></b>
                                            <input
                                                type={"text"}
                                                className="form-control"
                                                placeholder="Enter your country"
                                                name="country"
                                                value={country}
                                                onChange={(e) => onInputChange(e)}
                                                autoComplete="off"
                                                style={{ width: '230px', height: '2px', marginLeft: '50px', padding: '18px', fontSize: '14px' }}



                                            ></input>

                                        </div>
                                    </div>
                                </div>



                                <div className='row'>


                                    <div className='col-md-5'>
                                        <div className="mb-1">
                                            <b><label htmlFor="state" className="form-label">State</label></b>
                                            <input
                                                type={"text"}
                                                className="form-control"
                                                placeholder="Enter your emailid"
                                                name="state"
                                                value={state}
                                                onChange={(e) => onInputChange(e)}
                                                autoComplete="off"

                                                style={{ width: '230px', height: '2px', marginLeft: '5px', textalign: 'center', padding: '18px', fontSize: '14px' }}
                                            ></input>

                                        </div>
                                    </div>

                                    <div className='col-md-5'>
                                        <div className="mb-1">
                                            <b> <label htmlFor="password" style={{ width: '30px',marginright: '30px',marginLeft: '1px'}}className="form-label">Password</label></b>
                                            <input
                                                type={"text"}
                                                className="form-control"
                                                placeholder="Enter Password"
                                                name="password"
                                                value={password}
                                                onChange={onInputChange}
                                                autoComplete="off"
                                                style={{ width: '230px', height: '2px', marginLeft: '50px', padding: '18px', fontSize: '14px' }}
                                            ></input>
                                            {/* {errors.password && <div style={{ color: 'red' }}>{errors.password}
                                        </div>} */}

                                        </div>
                                    </div>
                                </div>




                                {/* <div className='row'>


                                <div className='col-md-5'>
                                    <div className="mb-1">
                                        <b><label htmlFor="emailId" className="form-label">Email Id</label></b>
                                        <input
                                            type={"email"}
                                            className="form-control"
                                            placeholder="Enter your emailid"
                                            name="emailId"
                                            value={emailId} disabled
                                            onChange={(e) => onInputChange(e)}
                                            style={{ width: '230px', height: '2px', marginLeft: '5px', textalign: 'center', padding: '18px', fontSize: '14px' }}
                                        ></input>

                                    </div>
                                </div>

                                <div className='col-md-5'>
                                    <div className="mb-1">
                                        <b><label htmlFor="role" className="form-label form-floating">Role</label></b>
                                        <input
                                            type={"text"}
                                            className="form-control"
                                            placeholder="Enter your lastname"
                                            name="role"
                                            value={role} disabled
                                            onChange={(e) => onInputChange(e)}
                                            style={{ width: '230px', height: '2px', marginLeft: '50px', padding: '18px', fontSize: '14px' }}
                                        ></input>

                                    </div>
                                </div>  </div>*/}

                                <div className='col-md-10' style={{textAlign:'center'}}>
                                    <div className="mb-1">
                                        <b><label style={{ width: '500px',marginLeft: '20px'}}htmlFor="memailId" className="form-label">Email Id</label></b>
                                        <input
                                            type={"email"}
                                            className="form-control"
                                            placeholder="Enter your emailid"
                                            name="emailId"
                                            value={emailId} disabled
                                            onChange={(e) => onInputChange(e)}
                                            style={{ width: '500px', height: '2px', marginLeft: '20px', padding: '18px', fontSize: '14px' }}
                                           
                                        ></input>

                                    </div>
                                </div>



                                <br></br>

                                <button type="submit" className="btn btn-outline-success  my-6">Submit</button>
                                <Link to="/mngprofile" type="submit" className="btn btn-outline-danger mx-2">Cancel</Link>

                            </form>
                        </div>

                    </div>
                </div>
            </div>

        </div>
    );
}

export default Editmanager;
