import { useState } from 'react';
import axios from 'axios';
// import ReactDatePicker from 'react-datepicker';
import DatePicker from 'react-datepicker';
import { Link } from "react-router-dom";
import { useNavigate } from 'react-router-dom';
import Employeedropdown from './Employeedropdown';
import "react-datepicker/dist/react-datepicker.css";
function Applyleave() {
    const navigate = useNavigate();
    const [leave, setLeave] = useState(
        {

            emailId: "",
            memailId: "",
            fromDate: "",
            toDate: "",
            leaveReason: "",
        }
    )
    const { emailId, memailId, fromDate, toDate, leaveReason } = leave
    const onInputChange = (e) => {
        setLeave({
            ...leave,
            [e.target.name]: e.target.value
        });
    }
    const handleSubmit = async (e) => {
        e.preventDefault();
        await axios.post(`http://localhost:9002/leave`, {

            "emailId": emailId,
            "memailId": memailId,
            "fromDate": fromDate,
            "toDate": toDate,
            "leaveReason": leaveReason,
        }
        )
            .then(res => {
                console.log(res.data)
                if (res.status === 404) {
                    alert(res.data)
                }
            })
            .catch(error => {
                console.log(error)
            })
        navigate("/employeehome");

    };
    return (
        <div>
            <Employeedropdown></Employeedropdown>
            <div className="container">
                <div className="row">
                    <div className="col-md-6 offset-md-3 border rounded p-4 mt-2 shadow">
                        <h2 className="text-center m-4"> Apply leave</h2>
                        <form onSubmit={(e) => handleSubmit(e)}>
                            <div className="mb-3">
                                <label htmlFor="emailId" className="form-label">Employee EmailId</label>
                                <input
                                    type={"text"}
                                    className="form-control"
                                    placeholder="Enter your emailid"
                                    name="emailId"
                                    value={emailId}
                                    onChange={onInputChange}
                                ></input>
                            </div>
                            <div className="mb-3">
                                <label htmlFor="memailId" className="form-label">Manager EmailId</label>
                                <input
                                    type={"text"}
                                    className="form-control"
                                    placeholder="Enter your emailid"
                                    name="memailId"
                                    value={memailId}
                                    onChange={onInputChange}
                                ></input>
                            </div>
                            <div className="mb-3">
                                <label htmlFor="leaveReason" className="form-label">leaveReason</label>
                                <input
                                    type={"text"}
                                    className="form-control"
                                    placeholder="Enter your managerId"
                                    name="leaveReason"
                                    value={leaveReason}
                                    onChange={onInputChange}
                                ></input>
                            </div>
                            <div className="mb-3">
                                
                                <div id="date-picker-example" className="md-form md-outline input-with-post-icon datepicker" inline="true">
                                <input placeholder="Select date" type="text" id="example" class="form-control"/>
                                <label htmlFor="fromDate" className="form-label">Start Date</label>
                                    <i class="fas fa-calendar input-prefix"></i>
                            </div>
                            </div>
                            

                            <button type="submit" className="btn btn-outline-info">Submit</button>
                            {/* <Link to="/"type="submit" className="btn btn-success">Submit</Link> */}
                            <Link to="/employeehome" type="submit" className="btn btn-outline-danger mx-2">Cancel</Link>
                        </form>
                    </div>

                </div>
            </div>
        </div>
    );
}

export default Applyleave;
