import Employeedropdown from './Employeedropdown';
import axios from "axios";
import React, { useState,useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import { useNavigate } from "react-router-dom";
function Editpageemployee(props) {

    const navigate = useNavigate();
    
    useEffect(() => {
        loadEmployee();
    }, [] );


    const [employee,setEmployee]=useState(
        {
            firstName:"",
            lastName:"",
            emailId:"",
            password:"",
            mobileNumber:"",
            gender:"",
            dateOfBirth:"",
            address:"",
            city:"",
            state:"",
            pinCode:"",
            country:"",
               
        }
    )

    const { firstName,lastName, emailId,password,mobileNumber,gender,dateOfBirth,address,city,state, pinCode,country}=employee;

    const onInputChange=(e)=>{
        setEmployee({...employee,
            [e.target.name]:e.target.value
        });
    }

    const onSubmit= async (e)=>{
        await axios.get(`http://localhost:8089/loginemployee/${localStorage.getItem("jwtToken")}`)
        .then(res => {
            console.log(res.data)
            axios.put(`http://localhost:8086/view/${res.data}`)
                .then(response => {
                    setEmployee(response.data)
                    console.log(response.data);
                })
        })
        .catch(error => {
            console.log(error);
        });

     navigate("/");
    };



    const loadEmployee = async () => {
        await axios.get(`http://localhost:8089/loginemployee/${localStorage.getItem("jwtToken")}`)
        .then(res => {
            console.log(res.data)
            axios.get(`http://localhost:8086/view/${res.data}`)
                .then(response => {
                    setEmployee(response.data)
                    console.log(response.data);
                })
        })
        .catch(error => {
            console.log(error);
        });

    }
    const divstyle={
        position:"absolute",
        marginleft: "50%",
         marginright: "500%",
         margintop: "10%",
         paddingright: "8.5%",
    }
    return (
        <div>
            <Employeedropdown></Employeedropdown>
            updateprofile
            {/* <div className="container">
            <div className="row">
                <div  className="col-md-6 offset-md-3 border rounded p-4 mt-2 shadow">
                    <h2  className="text-center m-4"> Edit Employee  </h2>

                    <form onSubmit={(e)=>onSubmit(e)}>
                    <div className="mb-3">
                        <label htmlFor="FirstName" className="form-label">FirstName</label>
                        <input
                        type={"text"}
                        className="form-control"
                        placeholder="Enter your employee firstname"
                        name="firstName"
                        value={firstName}
                        onChange={onInputChange}
                        ></input>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="LastName" className="form-label">LastName</label>
                        <input
                        type={"text"}
                        className="form-control"
                        placeholder="Enter your employeelastname"
                        name="lastName"
                        value={lastName}
                        onChange={onInputChange}
                        ></input>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="Email-Id" className="form-label">Email-Id</label>
                        <input
                        type={"text"}
                        className="form-control"
                        placeholder="Enter your emailid"
                        name="emailId"
                        value={emailId}
                        onChange={onInputChange}
                        ></input>
                    </div>
                    <button type="submit" className="btn btn-success">Submit</button>
                    <Link to="/" type="submit" className="btn btn-danger mx-2">Cancel</Link>
                    </form>
                </div>

            </div> </div> */}
              <div className="container" style={divstyle}>
                <div className="row">
                    <div className="col-md-7 offset-md-3 border rounded p-3 mt-3 shadow">
                        <h3 className="text-center m-6">{employee.firstName} {employee.lastName}'s  profile</h3>
                        <form onSubmit={(e)=>onSubmit(e)}>
                        <div className="card" >
                            <div className="card-header">
                                <ul className="list-group list-group-flush">
                                    <div className='row'>
                                        <div className='col-md-4'>
                                        <b>Id:</b>
                                            <li className="list-group-item">
                                                
                                                {employee.employeeId}
                                                
                                            </li>

                                        </div>

                                        <div className='col-md-7'>
                                        <b>EmailId:</b>
                                            <li className="list-group-item">
                                               
                                                {employee.emailId}
                                                
                                            </li>

                                        </div>
                                    </div>
                                    <br></br>
                                    <div className='row'>
                                        <div className='col-md-4'>
                                        <b>Contact Number:</b>
                                            <li className="list-group-item">
                                               
                                                {employee.mobileNumber}
                                                
                                            </li>

                                        </div>


                                        <div className='col-md-4'>
                                        <b>Password:</b>
                                            <li className="list-group-item">
                                                
                                                {employee.password}
                                            </li>
                                        </div>
                                        <div className='col-md-4'>
                                        <b>Role</b>
                                            <li className="list-group-item">
                                                
                                                {employee.role}
                                            </li>
                                        </div>
                                    </div>
                                    <br></br>
                                    <div className='row'>
                                        


                                        <div className='col-md-4'>
                                        <b>Gender:</b>
                                            <li className="list-group-item">
                                                
                                                {employee.gender}
                                            </li>
                                        </div>
                                        <div className='col-md-4'>
                                        <b>Date Of Birth:</b>
                                        
                                            <li className="list-group-item">
                                            {employee.dateOfBirth}
                                                
                                            </li>
                                        </div>
                                        <div className='col-md-4'>
                                        <b>Address:</b>
                                        
                                            <li className="list-group-item">
                                            {employee.address}
                                                
                                            </li>
                                        </div>
                                        
                                    </div> 
                                    <br></br>
                                    <div className='row'>
                                        <div className='col-md-4'>
                                        <b>City:</b>
                                            <li className="list-group-item">
                                               
                                                {employee.city}
                                                
                                            </li>

                                        </div>


                                        <div className='col-md-4'>
                                        <b>State:</b>
                                            <li className="list-group-item">
                                                
                                                {employee.state}
                                            </li>
                                        </div>
                                        <div className='col-md-4'>
                                        <b>Country</b>
                                            <li className="list-group-item">
                                                
                                                {employee.country}
                                            </li>
                                        </div>
                                    </div>

                                    <br></br>
                                   
                                </ul>
                            </div>
                        </div>
                        <Link className="btn btn-info my-2" to={"/edit"}>
                             Edit 
                        </Link>
                        </form>
                    </div>
                </div>
            </div>
         

























        </div>
    );
}

export default Editpageemployee;
