import React from 'react';
import { Link } from 'react-router-dom';
import { Nav, NavDropdown, Dropdown, DropdownButton, Button } from 'react-bootstrap'
import NavbarCollapse from 'react-bootstrap/esm/NavbarCollapse';
import './Employeedropdown.css';
import { useState,useEffect } from 'react';
import axios from 'axios';
function Employeedropdown() {

    

    const [employee, setEmployee] = useState('');

    useEffect(() => {
        loadEmployee();
    }, []);

    const loadEmployee = async () => {
        await axios.get(`http://localhost:8089/loginemployee/${localStorage.getItem("jwtToken")}`)
            .then(res => {
                console.log(res.data)
                axios.get(`http://localhost:8086/view/${res.data}`)
                    .then(response => {
                        setEmployee(response.data)
                        console.log(response.data);
                    })
            })
            .catch(error => {
                console.log(error);
            })

    };
    const fullname=`${employee.firstName} ${employee.lastName}`;
       
    return (
        <div>
            
            <nav style={{background:'#7986CB'}} className="navbar navbar-expand-lg navbar-light navbar-custom">
                <div className="container-fluid">
                    <a className="navbar-brand ">

                        Employee Management System
                    </a>
                </div>

                <NavbarCollapse id="basic-navbar-nav">
                    <Nav className="ml-auto">
                    </Nav>
                </NavbarCollapse>



                <DropdownButton title={fullname}  className="custom-dropdown-button" style={{ backgroundColor: 'white', color: 'black' }}>

                    {/* <NavDropdown.Divider></NavDropdown.Divider> */}
                    <Dropdown.Item >
                        <Link to="/displayemployee">
                            View Profile
                        </Link>
                    </Dropdown.Item>
                    <Dropdown.Item >
                        <Link to="/Applyleave">
                            Apply leave
                        </Link>
                    </Dropdown.Item>
                    <Dropdown.Item >
                        <Link to="/dispalyleave">
                            Leave Status
                        </Link>
                    </Dropdown.Item>
                    <Dropdown.Item >
                        <Link to="/employeetask">
                            Check Task
                        </Link>
                    </Dropdown.Item>
                </DropdownButton>
                <Nav className="ml-auto ms-2">
                    <Link to="/" ><Button style={{ backgroundColor: 'white', color: 'black' }} className='custom-button'>Logout</Button></Link>
                </Nav>

            </nav>

        </div>
    );
}

export default Employeedropdown;
