import { useState,useEffect6 } from 'react';
import axios from 'axios';
import { useNavigate,useParams } from 'react-router-dom';
import Managerdropdown from './Managerdropdown';
function Assigntask(props) {
    const navigate = useNavigate();
    
    const [employee, setEmployee] = useState(
        {
           emailId:"",

        }
    );
    const { emailId } = useParams();
    const [task, setTask] = useState(
        {

            taskName: "",
            description: "",
            dueDate: "",
            memailId:"",
        }
    )
    useEffect(() => {
        const loadEmployee = async () => {
            const reqdata = await fetch(`http://localhost:8086/view/${emailId}`);
            const res = reqdata.json();
            setEmployee(await res);
        }
        loadEmployee();
    },
        []);

        const empChange=(e)=>{
            setEmployee({
                ...employee,
                [e.target.name]: e.target.value
            });
        }
        // const{emailId}=employee;
    const { taskName, description,  dueDate,memailId } = task
    const onInputChange = (e) => {
        setTask({
            ...task,
            [e.target.name]: e.target.value
        });
    }
    const handleSubmit = async (e) => {
        e.preventDefault();
        await axios.post(`http://localhost:9010/task/${emailId}`, {

            "taskName": taskName,
            " description": description,
            " dueDate": dueDate,
            " memailId":memailId,
        }
        )
            .then(res => {
                console.log(res.data)
                if (res.status === 404) {
                    alert(res.data)
                }
            })
            .catch(error => {
                console.log(error)
            })
        navigate("/managerhome");

    };
    return (
        <div>
           <Managerdropdown></Managerdropdown>
            <div className="container">
                <div className="row">
                    <div className="col-md-6 offset-md-3 border rounded p-4 mt-2 shadow">
                        <h2 className="text-center m-4"> Assign Task</h2>
                        <form onSubmit={(e) => handleSubmit(e)}>
                            <div className="mb-3">
                            <div className="mb-3">
                                <label htmlFor="memailId" className="form-label">memailId</label>
                                <input
                                    type={"text"}
                                    className="form-control"
                                    placeholder="Enter yourManager emailId"
                                    name="memailId"
                                    value={memailId}
                                    onChange={onInputChange}
                                ></input>
                            </div>
                                <label htmlFor="taskName" className="form-label">TaskName</label>
                                <input
                                    type={"text"}
                                    className="form-control"
                                    placeholder="Enter your taskName"
                                    name="taskName"
                                    value={taskName}
                                    onChange={onInputChange}
                                ></input>
                            </div>
                            <div className="mb-3">
                                <label htmlFor="description" className="form-label">Manager EmailId</label>
                                <input
                                    type={"text"}
                                    className="form-control"
                                    placeholder="Enter description"
                                    name="description"
                                    value={description}
                                    onChange={onInputChange}
                                ></input>
                            </div>
                            <div className="mb-3">
                            <label htmlFor="dueDate" className="form-label">Due Date</label>
                                <input
                                    type={"Date"}
                                    className="form-control"
                                    placeholder="Enter your Date"
                                    name="dueDate"
                                    value={dueDate}
                                    onChange={onInputChange}
                                ></input>
                               
                            </div>
                          
                           
                           
                            

                            <button type="submit" className="btn btn-outline-info">Submit</button>
                            {/* <Link to="/"type="submit" className="btn btn-success">Submit</Link> */}
                            
                        </form>
                    </div>

                </div>
            </div>
        </div>
    );
}

export default Assigntask;
