import React from 'react';
import Employeedropdown from './Employeedropdown';

function Checktask(props) {

    const [employee, setEmployee] = useState([]);

    useEffect(() => {
        loadEmployee();
    }, []);

    const loadEmployee = async () => {
        await axios.get(`http://localhost:9000/loginmanager/${localStorage.getItem("jwtToken")}`)
            .then(res => {
                console.log(res.data)
                axios.get(`http://localhost:9001/assigned/${res.data}`)
                    .then(response => {
                        setEmployee(response.data)
                        console.log(response.data);
                    })
            })
            .catch(error => {
                console.log(error);
            });

    };
    return (
        <div>
            <Employeedropdown></Employeedropdown>
           hi check task 
           <div className="container">
        <div className="py-4">
            <table className="table table-striped table-hover " >
                <thead>
                    <tr>
                        <th scope="col">Id</th>
                        <th scope="col">FirstName</th>
                        <th scope="col">LastName</th>
                        <th scope="col">Email</th>
                        <th scope="col">Action</th>
                        

                        {/* <th scope="col">Action</th> */}
                    </tr>
                </thead>
                <tbody class="table-group-divider">
                    {
employee.map(x=>
    <tr>
                            <td>{x.employeeId}</td>
                            <td>{x.firstName}</td>
                            <td>{x.lastName}</td>
                            <td>{x.emailId}</td>
                            <td>
                                    <Link to={`/checktask/${x.emailId}`} className="btn btn-info mx-2">Assign Task</Link>
                                    <Link   to={`/applyleave/${x.emailId}`}className="btn btn-info mx-2">Leave status</Link>
                                    
                                    </td>
                        </tr>
    )
                        
                    }
                </tbody>
            </table>
        </div>
    </div>
        </div>
    );
}

export default Checktask;
