import React from 'react';
import { Link } from 'react-router-dom';
import { Nav, NavDropdown, Dropdown, DropdownButton, Button } from 'react-bootstrap'
import NavbarCollapse from 'react-bootstrap/esm/NavbarCollapse';
import './Managerdropdown.css';
import { useState,useEffect } from 'react';
import axios from 'axios';



function Managerdropdown(props) {



    const [mng, setMng] = useState('');

    useEffect(() => {
        loadMng();
    }, []);

    const loadMng = async () => {
        await axios.get(`http://localhost:9000/loginmanager/${localStorage.getItem("jwtToken")}`)
            .then(res => {
                console.log(res.data)
                axios.get(`http://localhost:8080/admin/manager/${res.data}`)
                    .then(response => {
                        setMng(response.data)
                        console.log(response.data);
                    })
            })
            .catch(error => {
                console.log(error);
            })

    };
    const fullname=`${mng.firstName} ${mng.lastName}`;
       









    return (
        <div>
        <nav className="navbar navbar-expand-lg navbar-light navbar-custom">
            <div className="container-fluid">
                <a className="navbar-brand ">

                    Employee Management System
                </a>
            </div>

            <NavbarCollapse id="basic-navbar-nav">
                <Nav className="ml-auto">
                </Nav>
            </NavbarCollapse>



            <DropdownButton title={fullname} style={{ color: 'black' }} className="custom-dropdown-button">

                {/* <NavDropdown.Divider></NavDropdown.Divider> */}
                <Dropdown.Item >
                    <Link to="/mngprofile">
                        View Profile
                    </Link>
                </Dropdown.Item>
                <Dropdown.Item >
                    <Link to="/checkleaveundermng">
                        check leaves
                    </Link>
                </Dropdown.Item>
                <Dropdown.Item >
                    <Link to="/checktask">
                        Assign Task
                    </Link>
                </Dropdown.Item>
                <Dropdown.Item >
                    <Link to="/managerhome">
                       All Employees
                    </Link>
                </Dropdown.Item>
            </DropdownButton>
            <Nav className="ml-auto ms-2">
                <Link to="/" ><Button style={{ backgroundColor: ' ', color: 'black' }} className='custom-button'>Logout</Button></Link>
            </Nav>

        </nav>

    </div>
    );
}
export default Managerdropdown;
