import { useState } from 'react';
import axios from 'axios';
import Navbar1 from '../Layout/Navbar1';
function Employeepage() {
    const[emp,setemp]=useState({
        emailId:"",
        password:""
     });
     
     const[isLoggedIn,setIsLoggedIn]=useState(false);
     const {emailId,password}=emp;
    
     const handleSubmit=async(event)=>{
        event.preventDefault();
        const response=await axios.post("http://localhost:8089/authenticate",
        {emailId,password}).then(res=>{
            console.log(res.data.jwt)
            localStorage.setItem("jwtToken",res.data.jwt);
            setIsLoggedIn(true);
            console.log(isLoggedIn);
            if(res===500){
                console.log("incorrect");
                alert("Incorrect email or password");
            }
            else{
                alert("Successfully logged in");
            }
             window.location.href="/AddEmployee";
              
        });
        console.log(response);
     }
    const emphandler=(event)=>{
        const name=event.target.name;
        const value=event.target.value;
        setemp({...emp,[name]:value});
    }
    
        return (
           <div>
     <Navbar1></Navbar1>
        
            <div className='div1'>
              
                <form className="form" onSubmit={ handleSubmit} >
                    <h3 className='h3'><b>Employee Login</b></h3>
                    <label for="emailId" ><b>emailId</b></label>
                    <input type="email" id="email" name="emailId" autoComplete='off' value={username} onChange={emphandler}></input>
                    <label for="password" ><b>Password</b></label>
                    <input type="password" name="password" id="password" autoComplete='off' value={password} onChange={emphandler}></input>
                    <button className="button" type="submit">Log In</button>
                </form>
    
            </div>
            </div>
        );
}
export default Employeepage;
