import Employeedropdown from './Employeedropdown';
import axios from "axios";
import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import { useNavigate } from "react-router-dom";
function Editpageemployee(props) {
    const navigate = useNavigate();
    const [employee, setEmployee] = useState(
        {    employeeId:"",
            firstName: "",
            lastName: "",
            password: "",
            mobileNumber: "",
            gender: "",
            memailId:"",
            dateOfBirth: "",
            address: "",
            city: "",
            state: "",
            pinCode: "",
            emailId:"",
            country: "",

        }
    );

    const[isFormValid,setIsFormValid]=useState(false);
    const[errors,setErrors]=useState({});
     const validateForm =()=>{
        const errors={};
        if(!emailId){
            errors.emailId='EmailId is required';

        }
        else if(!/\S+@\S+\.\S+/.test(emailId)){
errors.emailId='Email is invalid';
        }
       
        setErrors(errors);
        setIsFormValid(Object.keys(errors).length===0);
    };
    const { emailId } = useParams();
    useEffect(() => {
        const loadEmployee = async () => {
            const reqdata = await fetch(`http://localhost:8086/view/${emailId}`);

            const res = reqdata.json();
            setEmployee(await res);
        }
        loadEmployee();
    },
        []);




    const { employeeId, firstName, lastName, password, mobileNumber, memailId, gender, dateOfBirth, address, role, city, state, pinCode, country } = employee;

    const onInputChange = (e) => {
        setEmployee({
            ...employee,
            [e.target.name]: e.target.value
        });
    }

    const submithandler = async (e) => {
        e.preventDefault();
        console.log("hello hi ");
        if(isFormValid){
            console.log('submitting form',{emailId});
        }
        console.log(employee);
        const response = await axios.put(`http://localhost:8086/view/put/${emailId}`, { employeeId, firstName, lastName, password, mobileNumber, memailId, gender, dateOfBirth, address, role, city, state, pinCode, country });
        navigate("/displayemployee");
    };

    return (
        <div>
            <Employeedropdown></Employeedropdown>
            <br></br>
             <br></br>
            <div className="container">
                <div className="row">
                    <div className="col-md-6 offset-md-3 border rounded p-4 mt-2 shadow">
                        <h2 className="text-center "> Edit Employee  </h2>

                        <form className="bg-transparent" onSubmit={submithandler}>
                            <div className="mb-3">

                                <b> <label htmlFor="firstName" className="form-label">FirstName</label></b>
                                <input
                                    type={"text"}
                                    className="form-control"
                                    placeholder="Enter your firstname"
                                    name="firstName"
                                    value={firstName}
                                    onChange={(e) => onInputChange(e)}
                                ></input>
                            </div>
                            <div className="mb-3">
                                <b><label htmlFor="lastName" className="form-label">LastName</label></b>
                                <input
                                    type={"text"}
                                    className="form-control"
                                    placeholder="Enter your lastname"
                                    name="lastName"
                                    value={lastName}
                                    onChange={(e) => onInputChange(e)}
                                ></input>
                            </div>
                            <div className="mb-3">
                                <b><label htmlFor="employeeId" className="form-label">EmployeeId</label></b>
                                <input
                                    type={"text"}
                                    className="form-control"
                                    placeholder="Enter your lastname"
                                    name="employeeId"
                                    value={employeeId} disabled
                                    onChange={(e) => onInputChange(e)}
                                ></input>
                            </div>
                            <div className="mb-3">
                                <b><label htmlFor="memailId" className="form-label">Reporting Manager</label></b>
                                <input
                                    type={"email"}
                                    className="form-control"
                                    placeholder="Enter your lastname"
                                    name="memailId"
                                    value={memailId} disabled
