import { useState } from 'react';
import axios from 'axios';
import Navbar1 from '../Layout/Navbar1';
function Managerpage() {
    const [mng, setmng] = useState({
        emailId: "",
        password: ""
    });
    const [isFormValid, setIsFormValid] = useState(false);
    const [errors, setErrors] = useState({});
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const validateForm = () => {
        const errors = {};
        if (!emailId) {
            errors.emailId = 'EmailId is required';

        }
        else if (!/\S+@\S+\.\S+/.test(emailId)) {
            errors.emailId = 'Email is invalid';
        }
        if (!password) {
            errors.password = 'password is required';
        }
        setErrors(errors);
        setIsFormValid(Object.keys(errors).length === 0);
    };
    const { emailId, password } = mng;

    const handleSubmit = async (event) => {
        event.preventDefault();
        if(isFormValid){
            console.log('submitting form',{emailId,password});
        }
        const response = await axios.post("http://localhost:9000/authenticate",
            { emailId, password }).then(res => {
                console.log(res.data.jwt)
                localStorage.setItem("jwtToken", res.data.jwt);
                setIsLoggedIn(true);
                console.log(isLoggedIn);
                if (res === 500) {
                    console.log("incorrect");
                    alert("Incorrect email or password");
                }
                else {
                    alert("Successfully logged in");
                }
                window.location.href = "/managerhome";

            });
        console.log(response);
    }
    const mnghandler = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setmng({ ...mng, [name]: value });
    }

    return (
        <div>
            <Navbar1></Navbar1>
            <div style={{marginTop:'6rem'}}>
            <div className="container">
                <div className="row">
                    <div className="col-md-4 offset-md-4 border rounded  mt-4 shadow bg-success p-2 text-dark bg-opacity-10  ">
                        <h2 className="text-center m-4">Manager Login</h2>
                        <form onSubmit={(e) => handleSubmit(e)}  >
                            <div className="mb-3">
                                <label htmlFor='EmailId' className="form-label">EmailId</label>
                                <input
                                    type={"email"}
                                    className="form-control"
                                    placeholder="Enter your Email Id"
                                    name="emailId"
                                    value={emailId}
                                    style={{width:'200px',height:'3px',padding:'20px',fontSize:'14px'}}
                                    onChange={mnghandler}
                                ></input>
                                 {errors.emailId && <div style={{color:'red'}}>{errors.emailId}
                            </div>}
                            </div>
                            <div className="mb-3">
                                <label htmlFor="Password" className="form-label">Password</label>
                                <input
                                    type={"password"}
                                    className="form-control"
                                    placeholder="Enter your password"
                                    name="password"
                                    style={{fontSize:'14px'}}
                                    value={password}
                                    onChange={mnghandler}
                                ></input>
                                 {errors.password && <div style={{color:'red'}}>{errors.password}
                            </div>}
                            </div>
                            <button type="submit" onClick={validateForm} style={{background:'rgb(96, 197, 228)',color:'black'}} className="btn btn">Login</button>
                            <br></br>
                        </form>
                    </div>
                </div>
            </div>
        </div>
        </div>
    );
}
export default Managerpage;
