import Employeedropdown from './Employeedropdown';
import axios from "axios";
import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import { useNavigate  } from "react-router-dom";
function Editpageemployee(props) {

    const [employee, setEmployee] = useState(
        {
            firstName: "",
            lastName: "",
            password: "",
            mobileNumber: "",
            gender: "",
            dateOfBirth: "",
            address: "",
            city: "",
            state: "",
            pinCode: "",
            country: "",

        }
    );
    const navigate = useNavigate();
const {emailId}=useParams();
console.log("hello");
console.log(emailId);
    useEffect(() => {
        const loadEmployee=async()=>{
        const reqdata=await fetch();
        const res=reqdata.json();
        }
        loadEmployee();  
    },
     []);


   

    const { firstName, lastName,  password, mobileNumber, gender, dateOfBirth, address, city, state, pinCode, country } = employee;

    const onInputChange = (e) => {
        setEmployee({
            ...employee,
            [e.target.name]: e.target.value
        });
    }

    const submithandler= async (e) => {

        await axios.put(`http://localhost:8086/view/put/${emailId}`,employee)
        .then(res => {
            console.log(1);
            console.log(res.data)
            
            axios.post(`http://localhost:8086/view/put/${res.data}`)
                .then(response => {
                    setEmployee(response.data)
                    console.log(response.data);
                    console.log(2);
                })
        })
        .catch(error => {
            console.log(error);
        });





      //  await axios.get(`http://localhost:8089/loginemployee/${localStorage.getItem("jwtToken")}`)
           

        // navigate("/");
    };



    const loadEmployee  = async () => {
        await axios.get(`http://localhost:8089/loginemployee/${localStorage.getItem("jwtToken")}`)
            .then(res => {
                console.log(res.data)
                console.log("hi");
                axios.get(`http://localhost:8086/view/${res.data}`)
                    .then(response => {
                        setEmployee(response.data)
                        console.log(response.data);
                    })
            })
            .catch(error => {
                console.log(error);
            });

    }

    return (
        <div>
            <Employeedropdown></Employeedropdown>
            updateprofile
            <div className="container">
                <div className="row">
                    <div className="col-md-6 offset-md-3 border rounded p-4 mt-2 shadow">
                        <h2 className="text-center m-4"> Edit Employee  </h2>

                        <form onSubmit={submithandler}>
                            <div className="mb-3">

                                <label htmlFor="firstName" className="form-label">FirstName</label>
                                <input
                                    type={"text"}
                                    className="form-control"
                                    placeholder="Enter your firstname"
                                    name="firstName"
                                    value={firstName}
                                    onChange={onInputChange}
                                ></input>
                            </div>
                            <div className="mb-3">
                                <label htmlFor="lastName" className="form-label">LastName</label>
                                <input
                                    type={"text"}
                                    className="form-control"
                                    placeholder="Enter your lastname"
                                    name="lastName"
                                    value={lastName}
                                    onChange={onInputChange}
                                ></input>
                            </div>
                            <div className="mb-3">
                                <label htmlFor="emailId" className="form-label">EmailId</label>
                                <input
                                    type={"text"}
                                    className="form-control"
                                    placeholder="Enter your emailid"
                                    name="emailId"
                                    value={emailId}
                                    onChange={onInputChange}
                                ></input>
                            </div>
                        
                            <div className="mb-3">
                                <label htmlFor="password" className="form-label">Password</label>
                                <input
                                    type={"text"}
                                    className="form-control"
                                    placeholder="Enter your password"
                                    name="password"
                                    value={password}
                                    onChange={onInputChange}
                                ></input>
                            </div>
                            
                            <div className="mb-3">
                                <label htmlFor="dateOfBirth" className="form-label">dateOfBirth</label>
                                <input
                                    type={"text"}
                                    className="form-control"
                                    placeholder="Enter your dateOfBirth"
                                    name="dateOfBirth"
                                    value={dateOfBirth}
                                    onChange={onInputChange}
                                ></input>
                            </div>
                            
                            <div className="mb-3">
                                <label htmlFor="mobileNumber" className="form-label"> mobileNumber</label>
                                <input
                                    type={"text"}
                                    className="form-control"
                                    placeholder="Enter your  mobileNumber"
                                    name=" mobileNumber"
                                    value={mobileNumber}
                                    onChange={onInputChange}
                                ></input>
                            </div>
                            
                            <div className="mb-3">
                                <label htmlFor="gender" className="form-label">gender</label>
                                <input
                                    type={"text"}
                                    className="form-control"
                                    placeholder="Enter your gender"
                                    name="gender"
                                    value={gender}
                                    onChange={onInputChange}
                                ></input>
                            </div>
                            
                            <div className="mb-3">
                                <label htmlFor="address" className="form-label">address</label>
                                <input
                                    type={"text"}
                                    className="form-control"
                                    placeholder="Enter your address"
                                    name="address"
                                    value={address}
                                    onChange={onInputChange}
                                ></input>
                            </div>
                            <div className="mb-3">
                                <label htmlFor="city" className="form-label">city</label>
                                <input
                                    type={"text"}
                                    className="form-control"
                                    placeholder="Enter your city"
                                    name="city"
                                    value={city}
                                    onChange={onInputChange}
                                ></input>
                            </div>
                            <div className="mb-3">
                                <label htmlFor="state" className="form-label">state</label>
                                <input
                                    type={"text"}
                                    className="form-control"
                                    placeholder="Enter your state"
                                    name="state"
                                    value={state}
                                    onChange={onInputChange}
                                ></input>
                            </div>
                            <div className="mb-3">
                                <label htmlFor="pinCode" className="form-label">pinCode</label>
                                <input
                                    type={"text"}
                                    className="form-control"
                                    placeholder="Enter your pincode"
                                    name="pinCode"
                                    value={pinCode}
                                    onChange={onInputChange}
                                ></input>

                            </div>
                            <div className="mb-3">
                                <label htmlFor="country" className="form-label">country</label>
                                <input
                                    type={"text"}
                                    className="form-control"
                                    placeholder="Enter your country"
                                    name="country"
                                    value={country}
                                    onChange={onInputChange}
                                ></input>
                            </div>
                            <Link type="submit" className="btn btn-outline-info" to = {'/displayemployee'}>Submit</Link>
                            {/* <Link to="/"type="submit" className="btn btn-success">Submit</Link> */}
                            <Link to="/Addemployee" type="submit" className="btn btn-outline-danger mx-2">Cancel</Link>
                        </form>
                    </div>

                </div>
            </div>

























        </div>
    );
}

export default Editpageemployee;
