import React from 'react';
import axios from 'axios';
import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Employeedropdown from './Employeedropdown';

function Checktask(props) {

    const [employee, setEmployee] = useState([]);

    useEffect(() => {
        loadEmployee();
    }, []);

    const loadEmployee = async () => {
        await axios.get(`http://localhost:9000/loginmanager/${localStorage.getItem("jwtToken")}`)
            .then(res => {
                console.log(res.data)
                axios.get(`http://localhost:9011/check/${res.data}`)
                    .then(response => {
                        setEmployee(response.data)
                        console.log(response.data);
                    })
            })
            .catch(error => {
                console.log(error);
            });

    };
    return (
        <div>
            <Employeedropdown></Employeedropdown>
           hi check task 
           <div className="container">
        <div className="py-4">
            <table className="table table-striped table-hover " >
                <thead>
                    <tr>
                    "taskId": "47",
        "taskName": "dqwd",
        "description": "eqfde",
        "dueDate": "2023-03-31",
        "taskStatus": "NotCompleted",
        "taskProgress": null,
        "emailId": "Akshitha@gmail.com",
        "memailId": "Sravani@gmail.com"
                        <th scope="col">Task Id</th>
                        <th scope="col">Task Name</th>
                        <th scope="col">Description</th>
                        <th scope="col">Due Date</th>
                        <th scope="col">Task Status</th>
                        <th scope="col">Task Progress</th>
                        <th scope="col">Manager</th>

                        {/* <th scope="col">Action</th> */}
                    </tr>
                </thead>
                <tbody class="table-group-divider">
                    {
employee.map(x=>
    <tr>
                            <td>{x.employeeId}</td>
                            <td>{x.firstName}</td>
                            <td>{x.lastName}</td>
                            <td>{x.emailId}</td>
                            <td>
                                    <Link to={`/checktask/${x.emailId}`} className="btn btn-info mx-2">Assign Task</Link>
                                    <Link   to={`/applyleave/${x.emailId}`}className="btn btn-info mx-2">Leave status</Link>
                                    
                                    </td>
                        </tr>
    )
                        
                    }
                </tbody>
            </table>
        </div>
    </div>
        </div>
    );
}

export default Checktask;
