import axios from "axios";
import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";

const Employeeform=()=>{
    const navigate = useNavigate();
    const [employee,setEmployee]=useState(
        {
            firstName:"",
            lastName:"",
            emailId:"",
        }
    )
    const { firstName,lastName, emailId}=employee
    const onInputChange=(e)=>{
        setEmployee({...employee,
            [e.target.name]:e.target.value
        });
    }
    const onSubmit= async (e)=>{
     e.preventDefault();
     await axios.post("http://localhost:8080/api/v1/employee",employee)
     navigate("/");
    };
    return (
        <div className="container">
            <div className="row">
                <div  className="col-md-6 offset-md-3 border rounded p-4 mt-2 shadow">
                    <h2  className="text-center m-4"> Register Employee  </h2>
                    <form onSubmit={(e)=>onSubmit(e)}>
                    <div className="mb-3">

                        <label htmlFor="FirstName" className="form-label">FirstName</label>
                        <input
                        type={"text"}
                        className="form-control"
                        placeholder="Enter your employee firstname"
                        name="firstName"
                        value={firstName}
                        onChange={onInputChange}
                        ></input>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="LastName" className="form-label">LastName</label>
                        <input
                        type={"text"}
                        className="form-control"
                        placeholder="Enter your employeelastname"
                        name="lastName"
                        value={lastName}
                        onChange={onInputChange}
                        ></input>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="Email-Id" className="form-label">Email-Id</label>
                        <input
                        type={"text"}
                        className="form-control"
                        placeholder="Enter your emailid"
                        name="emailId"
                        value={emailId}
                        onChange={onInputChange}
                        ></input>
                    </div>
                    <button type="submit" className="btn btn-success">Submit</button>
                    <Link to="/"type="submit" className="btn btn-danger mx-2">Cancel</Link>
                    </form>
                </div>

            </div>
        </div> 
    );
}
export default Employeeform;
