import { useState } from 'react';
import axios from 'axios';
import Navbar1 from '../Layout/Navbar1';
function Employeepage() {
    const[emp,setemp]=useState({
        emailId:"",
        password:""
     });
      const[isFormValid,setIsFormValid]=useState(false);
    const[errors,setErrors]=useState({});
     const validateForm =()=>{
        const errors={};
        if(!emailId){
            errors.emailId='EmailId is required';

        }
        else if(!/\S+@\S+\.\S+/.test(emailId)){

        }
        if(!password){
            errors.password='password is required';
        }
        setErrors(errors);
        setIsFormValid(Object.keys(errors).length===0);
    };
     const[isLoggedIn,setIsLoggedIn]=useState(false);
     const {emailId,password}=emp;
    
     const handleSubmit=async(event)=>{
        event.preventDefault();
         if(isFormValid){
            console.log('submitting form',{username,password});
        }
        const response=await axios.post("http://localhost:8089/authenticate",
        {emailId,password}).then(res=>{
            console.log(res.data.jwt)
            localStorage.setItem("jwtToken",res.data.jwt);
            setIsLoggedIn(true);
            console.log(isLoggedIn);
            if(res===500){
                console.log("incorrect");
                alert("Incorrect email or password");
            }
            else{
                alert("Successfully logged in");
            }
             window.location.href="/employeehome";
              
        });
        console.log(response);
     }
    const emphandler=(event)=>{
        const name=event.target.name;
        const value=event.target.value;
        setemp({...emp,[name]:value});
    }
    
        return (
           <div>
     <Navbar1></Navbar1>
      <div style={{marginTop:'6rem'}}>
            <div className="container">
                <div className="row">
                    <div className="col-md-4 offset-md-4 border rounded  mt-4 shadow bg-success p-2 text-dark bg-opacity-10  ">
                        <h2 className="text-center m-4">Admin Login</h2>
                        <form onSubmit={(e) => handleSubmit(e)}  >
                            <div className="mb-3">
                                <b><label htmlFor="UserName" className="form-label">EmailId</label></b>
                                <input
                                    type={"email"}
                                    className="form-control"
                                    placeholder="Enter your EmailId"
                                    name="emailId"
                                    value={emailId}
                                    onChange={emphandler}
                                ></input>
                                 {errors.emailId && <div style={{color:'red'}}>{errors.emailId}
                            </div>}
                            </div>
                            <div className="mb-3">
                            <b><label htmlFor="Password" className="form-label">Password</label></b>
                                <input
                                    type={"password"}
                                    className="form-control"
                                    placeholder="Enter your password"
                                    name="password"
                                    value={password}
                                    onChange={emphandler}
                                ></input>
                                 {errors.password && <div style={{color:'red'}}>{errors.password}
                            </div>}
                            </div>
                            
                            <button type="submit" onClick={validateForm}className="btn btn-info  m-4">Login</button>
                           <br></br>
                        </form>
                    </div>

                </div>
            </div>
            </div>
            </div>
        );
}
export default Employeepage;
