import { useState } from 'react';
import axios from 'axios';
import Navbar1 from '../Layout/Navbar1';
import { Link } from "react-router-dom";
function Assignpage() {
    const [assign, setassign] = useState({
        emailId: "",
        memailId: ""
    });

    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const { emailId, memailId } = admin;

    const handleSubmit = async (e) => {
        e.preventDefault();
        await axios.post(`http://localhost:8080/admin/employee/addemployee`, {
            
            "emailId": emailId,
            "memailId":memailId, 
        }
        )
            .then(res => {
                console.log(res.data)
                if (res.status === 404) {
                    alert(res.data)
                }
            })
            .catch(error => {
                console.log(error)
            })
        navigate("/Addemployee");

    };
    const adminhandler = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setadmin({ ...admin, [name]: value });
    }

    return (
        <div>
            <Navbar1></Navbar1>
            <div className="container">
                <div className="row">
                    <div className="col-md-4 offset-md-4 border rounded  mt-2 shadow">
                        <h2 className="text-center m-4">Assign Manager</h2>
                        <form onSubmit={(e) => handleSubmit(e)}>
                            <div className="mb-3">

                                <label htmlFor="UserName" className="form-label">UserName</label>
                                <input
                                    type={"text"}
                                    className="form-control"
                                    placeholder="Enter your UserName"
                                    name="username"
                                    value={username}
                                    onChange={adminhandler}
                                ></input>
                            </div>
                            <div className="mb-3">
                                <label htmlFor="Password" className="form-label">Password</label>
                                <input
                                    type={"text"}
                                    className="form-control"
                                    placeholder="Enter your password"
                                    name="password"
                                    value={password}
                                    onChange={adminhandler}
                                ></input>
                            </div>
                            
                            <button type="submit" className="btn btn-info  m-4">Submit</button>
                            <br></br>
                        </form>
                    </div>

                </div>
            </div>
        </div>
    );
}

export default Assignpage;
