import { useState } from 'react';
import axios from 'axios';
import Navbar1 from '../Layout/Navbar1';
import { Link } from "react-router-dom";

function Adminpage() {
    const [admin, setadmin] = useState({
        username: "",
        password: ""
    });
    const[isFormValid,setIsFormValid]=useState(false);
    const[errors,setErrors]=useState({});
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const { username, password } = admin;

    const handleSubmit = async (event) => {
        event.preventDefault();
        const response = await axios.post("http://localhost:8080/authenticate",
            { username, password }).then(res => {
                console.log(res.data.jwt)
                localStorage.setItem("jwtToken", res.data.jwt);
                setIsLoggedIn(true);
                console.log(isLoggedIn);
                if (res === 500) {
                    console.log("incorrect");
                    alert("Incorrect username or password");
                }
                else {
                    alert("Successfully logged in");
                }
                window.location.href = "/AddEmployee";

            });
        console.log(response);
    }
    const adminhandler = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setadmin({ ...admin, [name]: value });
    }

    return (
        <div>
            <Navbar1></Navbar1>
            <div style={{marginTop:'6rem'}}>
            <div className="container">
                <div className="row">
                    <div className="col-md-4 offset-md-4 border rounded  mt-4 shadow bg-success p-2 text-dark bg-opacity-10  ">
                        <h2 className="text-center m-4">Admin Login</h2>
                        <form onSubmit={(e) => handleSubmit(e)}  >
                            <div className="mb-3">

                                <b><label htmlFor="UserName" className="form-label">UserName</label></b>
                                <input
                                    type={"text"}
                                    className="form-control success"
                                    placeholder="Enter your UserName"
                                    name="username"
                                    value={username}
                                    onChange={adminhandler}
                                ></input>
                            </div>
                            <div className="mb-3">
                            <b> <label htmlFor="Password" className="form-label">Password</label></b>
                                <input
                                    type={"password"}
                                    className="form-control"
                                    placeholder="Enter your password"
                                    name="password"
                                    value={password}
                                    onChange={adminhandler}
                                ></input>
                            </div>
                            {/* <div className="mb-3">
                                <b><label htmlFor="Password" className="form-label">Password</label></b>
                                <input type="radio" name="r1"></input>
                                <input type="radio" name="r1"></input>
                            </div> */}
                            
                            <button type="submit" className="btn btn-info  m-4">Login</button>
                            <br></br>
                        </form>
                    </div>

                </div>
            </div>
        </div>
        </div>
    );
}

export default Adminpage;
