import { useState } from 'react';
import axios from 'axios';
import Navbaradmin from '../Layout/Navbaradmin';
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";
function Assignpage() {
    const navigate = useNavigate();
    const [employee, setEmployee] = useState(
        {
            firstName: "",
            lastName: "",
            emailId: "",
            employeeId: "",
            password: "",
            role: "",
            memailId:"",

        }
    )

    const { firstName, lastName, emailId, employeeId, password, role,memailId } = employee
    const onInputChange = (e) => {
        setEmployee({
            ...employee,
            [e.target.name]: e.target.value
        });
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        await axios.post(`http://localhost:8080/admin/employee/addemployee`, {
            "firstName": firstName,
            "lastName": lastName,
            "emailId": emailId,
            "employeeId": employeeId,
            "password": password,
            "role": role,
        }
        )
            .then(res => {
                console.log(res.data)
                if (res.status === 404) {
                    alert(res.data)
                }
            })
            .catch(error => {
                console.log(error)
            })
        navigate("/Addemployee");

    };
    const adminhandler = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setassign({ ...assign, [name]: value });
    }

    return (
        <div>
            <Navbaradmin></Navbaradmin>
            <div className="container">
                <div className="row">
                    <div className="col-md-4 offset-md-4 border rounded  mt-2 shadow">
                        <h2 className="text-center m-4">Assign Manager</h2>
                        <form onSubmit={(e) => handleSubmit(e)}>
                            <div className="mb-3">

                                <label htmlFor="emailId" className="form-label">Employee EmailId</label>
                                <input
                                    type={"text"}
                                    className="form-control"
                                    placeholder="Enter your emailId"
                                    name="emailId"
                                    value={emailId}
                                    onChange={adminhandler}
                                ></input>
                            </div>
                            <div className="mb-3">
                                <label htmlFor="memailId" className="form-label">Manager EmailId</label>
                                <input
                                    type={"text"}
                                    className="form-control"
                                    placeholder="Enter your memailId"
                                    name="memailId"
                                    value={memailId}
                                    onChange={adminhandler}
                                ></input>
                            </div>
                            
                            <button type="submit" className="btn btn-info  m-4">Submit</button>
                            <br></br>
                        </form>
                    </div>

                </div>
            </div>
        </div>
    );
}

export default Assignpage;
