import { useState } from 'react';
import axios from 'axios';
import Navbar1 from '../Layout/Navbar1';
import { Link } from "react-router-dom";
function Adminpage() {
 const[admin,setadmin]=useState({
    username:"",
    password:""
 });
 
 const[isLoggedIn,setIsLoggedIn]=useState(false);
 const {username,password}=admin;

 const handleSubmit=async(event)=>{
    event.preventDefault();
    const response=await axios.post("http://localhost:8080/authenticate",
    {username,password}).then(res=>{
        console.log(res.data.jwt)
        localStorage.setItem("jwtToken",res.data.jwt);
        setIsLoggedIn(true);
        console.log(isLoggedIn);
        if(res===500){
            console.log("incorrect");
            alert("Incorrect username or password");
        }
        else{
            alert("Successfully logged in");
        }
         window.location.href="/AddEmployee";
          
    });
    console.log(response);
 }
const adminhandler=(event)=>{
    const name=event.target.name;
    const value=event.target.value;
    setadmin({...admin,[name]:value});
}

    return (
        <div>
 <Navbar1></Navbar1>
      <div className='login'>
       <form className="example-form" ><br></br>
        <label for="username">UserName</label>
        <input name="username" placeholder='Enter email Id' formControllerName="username"></input>
        <label for="password">UserName</label>
        <input name="password" placeholder='Enter password' formControllerName="password"></input>
        <button mat-raised-button color="primary">Submit</button><br></br>

       </form>
      </div>
        </div>
    );
}

export default Adminpage;
