import Employeedropdown from './Employeedropdown';
import axios from "axios";
import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import { useNavigate } from "react-router-dom";
function Editpageemployee(props) {
    const navigate = useNavigate();
    const [employee, setEmployee] = useState(
        {
            employeeId: "",
            firstName: "",
            lastName: "",
            password: "",
            mobileNumber: "",
            gender: "",
            memailId: "",
            dateOfBirth: "",
            address: "",
            city: "",
            state: "",
            pinCode: "",
            emailId: "",
            country: "",

        }
    );

    const [isFormValid, setIsFormValid] = useState(false);
    const [errors, setErrors] = useState({});
    const validateForm = () => {
        const errors = {};
        if (!emailId) {
            errors.emailId = 'EmailId is required';

        }
        else if (!/\S+@\S+\.\S+/.test(emailId)) {
            errors.emailId = 'Email is invalid';
        }

        setErrors(errors);
        setIsFormValid(Object.keys(errors).length === 0);
    };
    const { emailId } = useParams();
    useEffect(() => {
        const loadEmployee = async () => {
            const reqdata = await fetch(`http://localhost:8086/view/${emailId}`);

            const res = reqdata.json();
            setEmployee(await res);
        }
        loadEmployee();
    },
        []);




    const { employeeId, firstName, lastName, password, mobileNumber, memailId, gender, dateOfBirth, address, role, city, state, pinCode, country } = employee;

    const onInputChange = (e) => {
        setEmployee({
            ...employee,
            [e.target.name]: e.target.value
        });
    }

    const submithandler = async (e) => {
        e.preventDefault();
        console.log("hello hi ");
        if (isFormValid) {
            console.log('submitting form', { emailId });
        }
        console.log(employee);
        const response = await axios.put(`http://localhost:8086/view/put/${emailId}`, { employeeId, firstName, lastName, password, mobileNumber, memailId, gender, dateOfBirth, address, role, city, state, pinCode, country });
        navigate("/displayemployee");
    };

    return (
        <div>
            <Employeedropdown></Employeedropdown>
            <br></br>
            <br></br>
            <div className="container">
                <div className="row">
                    <div className="col-md-6 offset-md-3 border rounded p-4 mt-2 shadow">
                        <h2 className="text-center "> Edit Employee  </h2>
                        <form className="bg-transparent" onSubmit={submithandler}>





                            <div className='row'>
                                <div className='col-md-5'>
                                    <div className="mb-1">
                                        <b><label htmlFor="firstName" className="form-label">FirstName</label></b>
                                        <input
                                            type={"text"}
                                            className="form-control"
                                            placeholder="Enter Firstname"
                                            name="firstName"
                                            value={firstName}
                                            autoComplete="off"
                                            onChange={onInputChange}
                                            style={{ width: '230px', height: '2px', marginLeft: '5px', textalign: 'center', padding: '18px', fontSize: '14px' }}
                                        ></input>
                                        {errors.firstName && <div style={{ color: 'red' }}>{errors.firstName}
                                        </div>}
                                    </div>
                                </div>
                                <div className='col-md-5'>
                                    <div className="mb-1">
                                        <b><label htmlFor="lastName" className="form-label">LastName</label></b>
                                        <input
                                            type={"text"}
                                            className="form-control"
                                            placeholder="Enter Lastname"
                                            name="lastName"
                                            value={lastName}
                                            onChange={onInputChange}
                                            autoComplete="off"
                                            style={{ width: '230px', height: '2px', marginLeft: '50px', padding: '18px', fontSize: '14px' }}
                                        ></input>
                                        {errors.lastName && <div style={{ color: 'red' }}>{errors.lastName}
                                        </div>}
                                    </div>
                                </div>
                            </div>


                            <div className='row'>
                                <div className='col-md-5'>
                                    <div className="mb-1">
                                        <b> <label htmlFor="password" className="form-label">Password</label></b>
                                        <input
                                            type={"text"}
                                            className="form-control"
                                            placeholder="Enter Password"
                                            name="password"
                                            value={password}
                                            onChange={onInputChange}
                                            autoComplete="off"
                                            style={{ width: '230px', height: '2px', marginLeft: '5px', textalign: 'center', padding: '18px', fontSize: '14px' }}
                                        ></input>
                                        {errors.password && <div style={{ color: 'red' }}>{errors.password}
                                        </div>}

                                    </div>
                                </div>
                                <div className='col-md-5'>
                                    <div className="mb-1">
                                        <b><label htmlFor="dateOfBirth" className="form-label">DateOfBirth</label></b>

                                        <input
                                            type={"Date"}
                                            className="form-control"
                                            placeholder="Enter your dateOfBirth"
                                            name="dateOfBirth"
                                            value={dateOfBirth}
                                            onChange={(e) => onInputChange(e)}
                                            autoComplete="off"
                                            style={{ width: '230px', height: '2px', marginLeft: '50px', padding: '18px', fontSize: '14px' }}
                                        ></input>
                                        {/* {errors.password && <div style={{color:'red'}}>{errors.password}
                                            </div>} */}

                                    </div>

                                </div>
                            </div>
                            <div className='row'>
                                <div className='col-md-5'>
                                    <div className="mb-1">
                                        <b><label htmlFor="mobileNumber" className="form-label">Mobile Number</label></b>
                                        <input
                                            type={"text"}
                                            className="form-control"
                                            placeholder="Enter your mobileNumber"
                                            name="mobileNumber"
                                            value={mobileNumber}
                                            onChange={(e) => onInputChange(e)}
                                            autoComplete="off"
                                            style={{ width: '230px', height: '2px', marginLeft: '5px', textalign: 'center', padding: '18px', fontSize: '14px' }}
                                        ></input>
                                        {errors.password && <div style={{ color: 'red' }}>{errors.password}
                                        </div>}

                                    </div>
                                </div>
                                <div className='col-md-5'>
                                    <div className="mb-1">
                                        <b> <label htmlFor="gender" className="form-label">Gender</label></b>
                                        <input
                                            type={"text"}
                                            className="form-control"
                                            placeholder="Enter your gender"
                                            name="gender"
                                            value={gender}
                                            onChange={(e) => onInputChange(e)}
                                            autoComplete="off"
                                            style={{ width: '230px', height: '2px', marginLeft: '50px', padding: '18px', fontSize: '14px' }}
                                        ></input>
                                        {/* {errors.password && <div style={{color:'red'}}>{errors.password}
                                            </div>} */}

                                    </div>

                                </div>
                            </div>





                            <div className='row'>

                                <div className='col-md-5'>
                                    <div className="mb-1">
                                        <b>  <label htmlFor="address" className="form-label">Address</label></b>
                                        <input
                                            type={"text"}
                                            className="form-control"
                                            placeholder="Enter your address"
                                            name="address"
                                            value={address}
                                            onChange={(e) => onInputChange(e)}
                                            autoComplete="off"
                                            style={{ width: '230px', height: '2px', marginLeft: '5px', textalign: 'center', padding: '18px', fontSize: '14px' }}
                                        ></input>

                                    </div>

                                </div>
                                <div className='col-md-5'>
                                    <div className="mb-1">
                                        <b> <label htmlFor="city" className="form-label">City</label></b>
                                        <input
                                            type={"text"}
                                            className="form-control"
                                            placeholder="Enter your city"
                                            name="city"
                                            value={city}
                                            onChange={(e) => onInputChange(e)}
                                            autoComplete="off"
                                            style={{ width: '230px', height: '2px', marginLeft: '50px', padding: '18px', fontSize: '14px' }}
                                        ></input>


                                    </div>

                                </div>
                            </div>



                            <div className='row'>


                                <div className='col-md-5'>
                                    <div className="mb-1">
                                        <b> <label htmlFor="pinCode" className="form-label">PinCode</label></b>
                                        <input
                                            type={"text"}
                                            className="form-control"
                                            placeholder="Enter your pincode"
                                            name="pinCode"
                                            value={pinCode}
                                            onChange={(e) => onInputChange(e)}
                                            autoComplete="off"
                                            style={{ width: '230px', height: '2px', marginLeft: '5px', textalign: 'center', padding: '18px', fontSize: '14px' }}
                                        ></input>

                                    </div>
                                </div>

                                <div className='col-md-5'>
                                    <div className="mb-1">
                                        <b>  <label htmlFor="country" className="form-label">Country</label></b>
                                        <input
                                            type={"text"}
                                            className="form-control"
                                            placeholder="Enter your country"
                                            name="country"
                                            value={country}
                                            onChange={(e) => onInputChange(e)}
                                            autoComplete="off"
                                            style={{ width: '230px', height: '2px', marginLeft: '50px', padding: '18px', fontSize: '14px' }}



                                        ></input>

                                    </div>
                                </div>
                            </div>



                            <div className='row'>


                                <div className='col-md-5'>
                                    <div className="mb-1">
                                        <b><label htmlFor="state" className="form-label">State</label></b>
                                        <input
                                            type={"text"}
                                            className="form-control"
                                            placeholder="Enter your emailid"
                                            name="state"
                                            value={state} disabled
                                            onChange={(e) => onInputChange(e)}
                                            style={{ width: '230px', height: '2px', marginLeft: '5px', textalign: 'center', padding: '18px', fontSize: '14px' }}
                                        ></input>

                                    </div>
                                </div>

                                <div className='col-md-5'>
                                    <div className="mb-1">
                                        <b><label htmlFor="role" className="form-label">Manager</label></b>
                                        <input
                                            type={"text"}
                                            className="form-control"
                                            placeholder="Enter your lastname"
                                            name="role"
                                            value={role} disabled
                                            onChange={(e) => onInputChange(e)}
                                            style={{ width: '230px', height: '2px', marginLeft: '50px', padding: '18px', fontSize: '14px' }}
                                        ></input>

                                    </div>
                                </div>
                            </div>



                            <div className='row'>


                                <div className='col-md-5'>
                                    <div className="mb-1">
                                        <b><label htmlFor="emailId" className="form-label">EmailId</label></b>
                                        <input
                                            type={"email"}
                                            className="form-control"
                                            placeholder="Enter your emailid"
                                            name="emailId"
                                            value={emailId} disabled
                                            onChange={(e) => onInputChange(e)}
                                            style={{ width: '230px', height: '2px', marginLeft: '5px', textalign: 'center', padding: '18px', fontSize: '14px' }}
                                        ></input>

                                    </div>
                                </div>

                                <div className='col-md-5'>
                                    <div className="mb-1">
                                        <b><label htmlFor="role" className="form-label">Role</label></b>
                                        <input
                                            type={"text"}
                                            className="form-control"
                                            placeholder="Enter your lastname"
                                            name="role"
                                            value={role} disabled
                                            onChange={(e) => onInputChange(e)}
                                            style={{ width: '230px', height: '2px', marginLeft: '50px', padding: '18px', fontSize: '14px' }}
                                        ></input>

                                    </div>
                                </div>
                            </div>




                            <button type="submit" className="btn btn-outline-success">Submit</button>
                            <Link to="/displayemployee" type="submit" className="btn btn-outline-danger mx-2">Cancel</Link>

                        </form>


                    </div>

                </div>
            </div>


            <h1></h1>
        </div>
    );

}

export default Editpageemployee;
