import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState, useEffect } from "react";
import axios from 'axios';
import Managerdropdown from './Managerdropdown';
import { useParams } from 'react-router-dom';

function Taskprogresscheck(props) {
    const [progress, setProgress] = useState([]);

    useEffect(() => {
        loadProgress();
    }, []);

    const loadProgress = async () => {
        await axios.get(`http://localhost:8089/loginemployee/${localStorage.getItem("jwtToken")}`)
            .then(res => {
                console.log("employee");
                console.log(res.data);
                axios.get(`http://localhost:9011/check/${res.data}`)
                    .then(response => {
                        setProgress(response.data)
                        console.log(response.data);
                    })
            })
            .catch(error => {
                console.log(error);
            });

    };


    return (
        <div>
            <Managerdropdown></Managerdropdown>
            taskprogress
            <div className="container">
                <div className="py-4">
                    <table className="table table-striped table-hover " >
                        <thead>
                            <tr>
                                <th scope="col">Task Id</th>
                                <th scope="col">Task Name</th>
                                <th scope="col">Description</th>
                                <th scope="col">Due Date</th>
                                <th scope="col">Task Status</th>
                                <th scope="col">Task Progress</th>
                                {/* <th scope="col">Action</th> */}
                            </tr>
                        </thead>
                        <tbody class="table-group-divider">
                            {
                                progress.map(x =>
                                    <tr>
                                        <td>{x.taskId}</td>
                                        <td>{x.taskName}</td>
                                        <td>{x.description}</td>
                                        <td>{x.dueDate}</td>
                                        <td>{x.taskStatus}</td>
                                        <td>{x.taskProgress}</td>
                                        {/* <td>
                                            <Link to={`/updateprogress`} className="btn btn-outline-success mx-2">Accept</Link>
                                            <Link to={`/updateprogress`} className="btn btn-outline-danger mx-2">Reject</Link>

                                        </td> */}
                                    </tr>
                                )

                            }
                        </tbody>
                    </table>
                </div>
            </div>
            
        </div>
    );
}

export default Taskprogresscheck;
