import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Navbar2 from '../Layout/Navbar2';
import { Link,useParams } from "react-router-dom";
import {  Button } from 'react-bootstrap'


function AddEmployee(props) {
  const [employees, setEmployees] = useState([]);
  const {id}=useParams();
  useEffect(() => {
      loadEmployees();
  }, []);

  const loadEmployees = async () => {
      const result = await axios.get('http://localhost:8080/admin/employee/getemployees');
      console.log(result.data);
      setEmployees(result.data);
  }
  return (
    <div>
        <Navbar2></Navbar2>
      <br></br>
      <div className="container">
        <div className="row">
          <div className="col-md-4 offset-md-4 border rounded  mt-2 shadow">
            <form >
              <div className="mb-10">
                <br></br>
               <Link to="/employeeform"> <Button style={{marginRight:'50px',backgroundColor:'rgb(96, 197, 228'}}>AddEmployee</Button></Link>
                <Link  to="/managerform"><Button style={{backgroundColor:'rgb(96, 197, 228'}}>Add Manager</Button></Link>
              </div>
              <br></br>
            </form>
          </div>

        </div>
      </div>

 <div className="container">
            <div className="py-4">
                <table className="table table-striped table-hover " >
                    <thead>
                        <tr>
                            <th scope="col">Id</th>
                            <th scope="col">FirstName</th>
                            <th scope="col">LastName</th>
                            <th scope="col">Email</th>
                            <th scope="col">Action</th>
                        </tr>
                    </thead>
                    <tbody class="table-group-divider">
                        {
                            employees.map((employees, index) => (
                                <tr>
                                    {/* <th scope="row" key={index}> {index + 1} </th> */}
                                    <td>{employees.id}</td>
                                    <td>{employees.firstName}</td>
                                    <td>{employees.lastName}</td>
                                    <td>{employees.emailId}</td>
                                    <td>
                                    <Link to={`/viewemployee/${employees.id}`} className="btn btn-primary mx-2">View</Link>
                                    <Link   to={`/editemployee/${employees.id}`}className="btn btn-light mx-2"  >Edit</Link>
                                    <button type="button" className="btn btn-danger mx-2"
                                    onClick={()=>deleteEmployee(employees.id)}>Delete</button>
                                    </td>
                                   
                                </tr>
                            ))
                        }
                    </tbody>
                </table>
            </div>
        </div>






















      </div>
  );
}

export default AddEmployee;
