import { useState } from 'react';
import axios from 'axios';
import Navbar1 from '../Layout/Navbar1';
function Managerpage() {
    const[mng,setmng]=useState({
        emailId:"",
        password:""
     });
     
     const[isLoggedIn,setIsLoggedIn]=useState(false);
     const {emailId,password}=mng;
    
     const handleSubmit=async(event)=>{
        event.preventDefault();
        const response=await axios.post("http://localhost:9000/authenticate",
        {emailId,password}).then(res=>{
            console.log(res.data.jwt)
            localStorage.setItem("jwtToken",res.data.jwt);
            setIsLoggedIn(true);
            console.log(isLoggedIn);
            if(res===500){
                console.log("incorrect");
                alert("Incorrect email or password");
            }
            else{
                alert("Successfully logged in");
            }
             window.location.href="/AddEmployee";
              
        });
        console.log(response);
     }
    const mnghandler=(event)=>{
        const name=event.target.name;
        const value=event.target.value;
        setmng({...mng,[name]:value});
    }
    
        return (
           <div>
     <Navbar1></Navbar1>
     <div className="container">
                <div className="row">
                    <div className="col-md-4 offset-md-4 border rounded  mt-2 shadow">
                        <h2 className="text-center m-4">Manager Login</h2>
                        <form onSubmit={(e) => handleSubmit(e)}>
                            <div className="mb-3">
                                <label htmlFor='EmailId' className="form-label">EmailId</label>
                                <input
                                    type={"text"}
                                    className="form-control"
                                    placeholder="Enter your Email Id"
                                    name="emailId"
                                    value={emailId}
                                    onChange={mnghandler}
                                ></input>
                            </div>
                            <div className="mb-3">
                                <label htmlFor="Password" className="form-label">Password</label>
                                <input
                                    type={"text"}
                                    className="form-control"
                                    placeholder="Enter your password"
                                    name="password"
                                    value={password}
                                    onChange={mnghandler}
                                ></input>
                            </div>
                            
                            <button type="submit" className="btn btn-info  m-4">Login</button>
                            <br></br>
                        </form>
                    </div>

                </div>
            </div>
            </div>
        );
}
export default Managerpage;
