import { useState } from 'react';
import axios from 'axios';
import Navbar1 from '../Layout/Navbar1';
import './Adminpage.css';
function Adminpage() {
 const[admin,setadmin]=useState({
    username:"",
    password:""
 });
 
 const[isLoggedIn,setIsLoggedIn]=useState(false);
 const {username,password}=admin;

 const handleSubmit=async(event)=>{
    event.preventDefault();
    const response=await axios.post("http://localhost:8080/authenticate",
    {username,password}).then(res=>{
        console.log(res.data.jwt)
        localStorage.setItem("jwtToken",res.data.jwt);
        setIsLoggedIn(true);
        console.log(isLoggedIn);
        if(res===500){
            console.log("incorrect");
            alert("Incorrect username or password");
        }
        else{
            alert("Successfully logged in");
        }
         window.location.href="/AddEmployee";
          
    });
    console.log(response);
 }
const adminhandler=(event)=>{
    const name=event.target.name;
    const value=event.target.value;
    setadmin({...admin,[name]:value});
}

    return (
       <div>
  <Navbar1></Navbar1>
    
        <div className='div1'>
          
            <form className="form" onSubmit={ handleSubmit} >
                <h3 className='h3'><b>Admin Login</b></h3>
                <label for="username" ><b>Username</b></label>
                <input id="username" name="username" autoComplete='off' value={username} onChange={adminhandler}></input>
                <label for="password" ><b>Password</b></label>
                <input type="password" name="password" id="password" autoComplete='off' value={password} onChange={adminhandler}></input>
                <button className="button" type="submit">Log In</button>
            </form>

        </div>
        </div>
    );
}

export default Adminpage;
