import React from 'react';
import axios from 'axios';
import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Managerdropdown from './Managerdropdown';
function Checkleaveundermanager(props) {
    const [display, setDisplay] = useState([]);

    useEffect(() => {
        loadLeave();
    }, []);

    const loadLeave = async () => {
        await axios.get(`http://localhost:9000/loginmanager/${localStorage.getItem("jwtToken")}`)
            .then(res => {
                console.log(res.data)
                axios.get(`http://localhost:9003/updatestatus/${res.data}`)
                    .then(response => {
                        setDisplay(response.data)
                        console.log(response.data);
                    })
            })
            .catch(error => {
                console.log(error);
            });

    };











    
    return (
        <div>
            <Managerdropdown></Managerdropdown>
             <div className="container">
                <div className="py-4">
                    <table className="table table-striped table-hover " >
                        <thead>
                            <tr>
                                <th scope="col">Leave Id</th>
                                <th scope="col"> From Date</th>
                                <th scope="col">To Date</th>
                                <th scope="col">Leave Reason</th>
                                <th scope="col">Leave status</th>
                                <th scope="col">Action</th>
                               


                            </tr>
                        </thead>
                        <tbody class="table-group-divider">
                            {
                                display.map(x =>
                                    <tr>
                                        <td>{x.leaveId}</td>
                                        <td>{x.fromDate}</td>
                                        <td>{x.toDate}</td>
                                        <td>{x.leaveReason}</td>
                                        <td>{x.leaveStatus}</td>
                                        <td>
                                            <Link to={`/updateprogress`} className="btn btn-success mx-2">Accept</Link>
                                            <Link to={`/updateprogress`} className="btn btn-danger mx-2">Reject</Link>

                                        </td>
                                    </tr>
                                )

                            }
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
}

export default Checkleaveundermanager;
