import Employeedropdown from './Employeedropdown';
import axios from "axios";
import React, { useState,useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import { useNavigate } from "react-router-dom";
function Editpageemployee(props) {
    const [employee, setEmployee] = useState('');

    useEffect(() => {
        loadEmployee();
    }, []);

    const loadEmployee = async () => {
        await axios.get(`http://localhost:8089/loginemployee/${localStorage.getItem("jwtToken")}`)
            .then(res => {
                console.log(res.data)
                axios.get(`http://localhost:8086/view/${res.data}`)
                    .then(response => {
                        setEmployee(response.data)
                        console.log(response.data);
                    })
            })
            .catch(error => {
                console.log(error);
            });

    };
    const navigate = useNavigate();
    const {id}=useParams();
    
    useEffect(() => {
        loadEmployees();
    }, [] );


    // const [employee,setEmployee]=useState(
    //     {
    //         firstName:"",
    //         lastName:"",
    //         emailId:"",
    //     }
    // )

    const { firstName,lastName, emailId}=employee;

    const onInputChange=(e)=>{
        setEmployee({...employee,
            [e.target.name]:e.target.value
        });
    }

    const onSubmit= async (e)=>{
     e.preventDefault();
     await axios.put(`http://localhost:8080/api/v1/employees/${id}`,employee)
     navigate("/");
    };



    const loadEmployees = async () => {
        const result = await axios.get(`http://localhost:8080/api/v1/employees/${id}`);
        //console.log(result.data);
        setEmployee(result.data);
    }





    return (
        <div>
            <Employeedropdown></Employeedropdown>
            updateprofile
            <div className="container">
            <div className="row">
                <div  className="col-md-6 offset-md-3 border rounded p-4 mt-2 shadow">
                    <h2  className="text-center m-4"> Edit Employee  </h2>

                    <form onSubmit={(e)=>onSubmit(e)}>
                    <div className="mb-3">
                        <label htmlFor="FirstName" className="form-label">FirstName</label>
                        <input
                        type={"text"}
                        className="form-control"
                        placeholder="Enter your employee firstname"
                        name="firstName"
                        value={firstName}
                        onChange={onInputChange}
                        ></input>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="LastName" className="form-label">LastName</label>
                        <input
                        type={"text"}
                        className="form-control"
                        placeholder="Enter your employeelastname"
                        name="lastName"
                        value={lastName}
                        onChange={onInputChange}
                        ></input>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="Email-Id" className="form-label">Email-Id</label>
                        <input
                        type={"text"}
                        className="form-control"
                        placeholder="Enter your emailid"
                        name="emailId"
                        value={emailId}
                        onChange={onInputChange}
                        ></input>
                    </div>
                    <button type="submit" className="btn btn-success">Submit</button>
                    <Link to="/" type="submit" className="btn btn-danger mx-2">Cancel</Link>
                    </form>
                </div>

            </div>
        </div> 

























        </div>
    );
}

export default Editpageemployee;
