import axios from "axios";
import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";

const Employeeform=()=>{
    const navigate = useNavigate();
    const[first_name,setfirstName]=useState('');
    const[last_name,setlastName]=useState('');
    const[email_id,setemailId]=useState('');
    const[employee_id,setemployeeId]=useState('');
    const[password,setpassword]=useState('');
    const[role,setrole]=useState('');
    const[dob,setdateOfBirth]=useState('');
    const[address,setaddress]=useState('');
    const[city,setcity]=useState('');
    const[state,setstate]=useState('');
    const[pinCode,setpinCode]=useState('');
    const[country,setcountry]=useState('');

    function handleSubmit(e){
        e.preventDefault();
        axios.post(`http://localhost:8080/admin/employee/addemployee`,{
            "firstName":firstName,
            "lastName":lastName,
            "emailId":emailId,
            "employeeId":employeeId,
            "password":password,
            "role":role,
            "dateOfBirth":dateOfBirth,
            "address": address,
            "city": city,
            "state":state,
            "pinCode":pinCode,
            "country":country
        }).then(res=>{
            console.log(res.data)
            if(res.status===403){
                alert(res.data)
            }
        })
        .catch(error=>{
            console.log(error)
        })
    }
   const firstNamehandler=(event)=>{
    setfirstName(event.target.value);
   }
   const lastNamehandler=(event)=>{
    setlastName(event.target.value);
   }
   const emailIdhandler=(event)=>{
    setemailId(event.target.value);
   }
   const employeeIdhandler=(event)=>{
    setemployeeId(event.target.value);
   }
   const passwordhandler=(event)=>{
    setpassword(event.target.value);
   }
   const rolehandler=(event)=>{
    setrole(event.target.value);
   }
   const dateOfBirthhandler=(event)=>{
    setdateOfBirth(event.target.value);
   }
   const addresshandler=(event)=>{
    setaddress(event.target.value);
   }
   const cityhandler=(event)=>{
    setcity(event.target.value);
   }
   const statehandler=(event)=>{
    setstate(event.target.value);
   }
   const pinCodehandler=(event)=>{
    setpinCode(event.target.value);
   }
   const countryhandler=(event)=>{
    setcountry(event.target.value);
   }

    return (
        <div className="container">
            <div className="row">
                <div  className="col-md-6 offset-md-3 border rounded p-4 mt-2 shadow">
                    <h2  className="text-center m-4"> Register Employee  </h2>
                    <form onSubmit={(e)=>handleSubmit(e)}>
                    <div className="mb-3">

                        <label htmlFor="FirstName" className="form-label">FirstName</label>
                        <input
                        type={"text"}
                        className="form-control"
                        placeholder="Enter your employee firstname"
                        name="firstName"
                        value={firstName}
                        onChange={firstNamehandler}
                        ></input>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="LastName" className="form-label">LastName</label>
                        <input
                        type={"text"}
                        className="form-control"
                        placeholder="Enter your employeelastname"
                        name="lastName"
                        value={lastName}
                        onChange={lastNamehandler}
                        ></input>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="EmailId" className="form-label">EmailId</label>
                        <input
                        type={"text"}
                        className="form-control"
                        placeholder="Enter your emailid"
                        name="emailId"
                        value={emailId}
                        onChange={emailIdhandler}
                        ></input>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="employeeId" className="form-label">employeeId</label>
                        <input
                        type={"text"}
                        className="form-control"
                        placeholder="Enter your emailid"
                        name="employeeId"
                        value={employeeId}
                        onChange={employeeIdhandler}
                        ></input>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="password" className="form-label">password</label>
                        <input
                        type={"text"}
                        className="form-control"
                        placeholder="Enter your emailid"
                        name="password"
                        value={password}
                        onChange={passwordhandler}
                        ></input>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="role" className="form-label">role</label>
                        <input
                        type={"text"}
                        className="form-control"
                        placeholder="Enter your emailid"
                        name="role"
                        value={role}
                        onChange={rolehandler}
                        ></input>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="dateOfBirth" className="form-label">dateOfBirth</label>
                        <input
                        type={"text"}
                        className="form-control"
                        placeholder="Enter your emailid"
                        name="dateOfBirth"
                        value={dateOfBirth}
                        onChange={dateOfBirthhandler}
                        ></input>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="address" className="form-label">address</label>
                        <input
                        type={"text"}
                        className="form-control"
                        placeholder="Enter your emailid"
                        name="address"
                        value={address}
                        onChange={addresshandler}
                        ></input>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="city" className="form-label">city</label>
                        <input
                        type={"text"}
                        className="form-control"
                        placeholder="Enter your emailid"
                        name="city"
                        value={city}
                        onChange={cityhandler}
                        ></input>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="state" className="form-label">state</label>
                        <input
                        type={"text"}
                        className="form-control"
                        placeholder="Enter your emailid"
                        name="state"
                        value={state}
                        onChange={statehandler}
                        ></input>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="pinCode" className="form-label">pinCode</label>
                        <input
                        type={"text"}
                        className="form-control"
                        placeholder="Enter your emailid"
                        name="pinCode"
                        value={pinCode}
                        onChange={pinCodehandler}
                        ></input>
                        
                    </div>
                    <div className="mb-3">
                        <label htmlFor="country" className="form-label">country</label>
                        <input
                        type={"text"}
                        className="form-control"
                        placeholder="Enter your emailid"
                        name="country"
                        value={country}
                        onChange={countryhandler}
                        ></input>
                    </div>
                    <Link to="/"type="submit" className="btn btn-success">Submit</Link>
                    </form>
                </div>

            </div>
        </div> 
    );
}
export default Employeeform;
