import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState, useEffect } from "react";
import axios from 'axios';
import {useNavigate, useParams } from 'react-router-dom';
import Employeedropdown from './Employeedropdown';
function Updataskform(props) {



    const [progress, setProgress] = useState({
        taskProgress:"",
    });

const {taskId}=useParams;
const{taskProgress}=progress
const onInputChange = (e) => {
    setProgress({
        ...progress,
        [e.target.name]: e.target.value
    });
}
const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.put(`http://localhost:9011/check/update/${taskId}`, progress
    )
        .then(res => {
            console.log(res.data)
            if (res.status === 404) {
                alert(res.data)
            }
        })
        .catch(error => {
            console.log(error)
        })
    navigate("/employeetask");

};

    return (
        <div>
            <Employeedropdown></Employeedropdown>
            <div className="container" >
                <div className="row">
                    <div className="col-md-6 offset-md-3 border rounded p-4 mt-2 shadow">
                        <h2 className="text-center m-4"> Assign Task</h2>
                        <form onSubmit={(e) => handleSubmit(e)} >
                        <div className="mb-3">
                                <label htmlFor="taskProgress" className="form-label">taskProgress</label>
                                <input
                                    type={"text"}
                                    className="form-control"
                                    placeholder="Enter your taskProgress"
                                    name="taskProgress"
                                    value={taskProgress}
                                    onChange={onInputChange}
                                ></input>
                            </div>
                            <button type="submit" className="btn btn-outline-success">Proceed</button>
                            {/* <Link to="/"type="submit" className="btn btn-success">Submit</Link> */}
                            
                        </form>
                            </div>
                            </div>
                            
        </div> </div>
    );
}

export default Updataskform;
